use std::collections::HashSet;

fn main() {
    
    let signal = String::from("");
    let signal_length: i32 = (signal.chars().count() - 3 )as i32;
    const MARKER_LENGTH: i32 = 4;
    let mut char_pos: i32 = 0;

    while char_pos < signal_length {
        let subsignal: String = signal.chars().skip(char_pos as usize).take(MARKER_LENGTH as usize).collect();
        if contains_only_unique_chars(subsignal) {
            break
        }
        char_pos += 1;
    }

    println!("seen_chars: {:?}", char_pos + MARKER_LENGTH);
}


fn contains_only_unique_chars(_string: String) -> bool {
    let set: HashSet<char> = _string.chars().collect();

    return set.len() == _string.chars().count();
}